'use strict';


// DATABASE //

/**
* FUNCTION DB( tables )
*	Creates a database containing multiple document stores.
*
* @constructor
* @param {Array} tables - array of table names
* @returns {DB} Database instance
*/
function DB( tables ) {
	var self = this;
	self._tables = tables;
	tables.forEach( function( e ) {
		self.e = self.create( e );
	});
} // end FUNCTION DB()


// METHODS //

DB.prototype.create = require( './create.js' );
DB.prototype.reset = require( './reset.js' );


// EXPORTS //

module.exports = DB;
