'use strict';

/**
* Resets all tables of the database.
*
* @returns {DB} database instance
*/
function reset() {
	/* jshint validthis:true */
	var table;
	var self = this;

	self._tables.forEach( function( name ) {
		table = self[ name.toUpperCase() ];
		table.remove({ }, { multi: true }, function onRemoved() {
			table.loadDatabase( function() {
				// resetted data table.
			});
		});
	});
	return self;
} // end FUNCTION reset()


// EXPORTS //

module.exports = reset;
