'use strict';

/**
* Increases the progressbar by the supplied value.
*
* @param {number} val - increase amount
* @returns {Void}
*/
function increment( val ) {
	/* jshint validthis:true */
	var progBar = this.$container;
	var currentVal = parseInt( progBar[0].style.width, 10 );
	progBar[0].style.width = ( currentVal + val ) + '%';
} // end FUNCTION increment()


// EXPORTS //

module.exports = increment;
