'use strict';

// MODULES //

var $ = require( 'jquery' );


// PROGRESS BAR //

/**
* FUNCTION Progressbar( config )
*	Returns a progressbar instance.
*
* @constructor
* @param {String} id - id of progressbar div
* @returns {Progressbar} progressbar instance
*/
function Progressbar( id ) {
	this.$container = $( '#' + id );
} // end FUNCTION Progressbar()


// METHODS //

Progressbar.prototype.increment = require( './increment.js' );
Progressbar.prototype.setText = require( './setText.js' );
Progressbar.prototype.setValue = require( './setValue.js' );
Progressbar.prototype.reset = require( './reset.js' );

// EXPORTS //

module.exports = Progressbar;
