'use strict';

/**
* FUNCTION setText( text )
*	Sets the progressbar text to the supplied value.
*
* @param {String} text - input text
* @returns {Void}
*/
function setText( text ) {
	/* jshint validthis:true */
	this.$container.text( text );
} // end FUNCTION setText()


// EXPORTS //

module.exports = setText;
