'use strict';

/**
* FUNCTION setText( val )
*	Sets the progressbar value to the supplied percent value.
*
* @param {Number} val - input percent value
* @returns {Void}
*/
function setText( val ) {
	/* jshint validthis:true */
	this.$container[0].style.width = val + '%';
} // end FUNCTION setText()


// EXPORTS //

module.exports = setText;
